import React, {useState, useEffect} from "react"
import Header from "./components/Header.js"
import InfoBox from "./components/InfoBox.js"
import Map from "./components/Map.js"
import Panel from "./components/Panel.js"
import {FormControl, Select, MenuItem, Card, CardContent} from "@material-ui/core"
import "./App.css"

function App() {

  const [countries, setCountries] = useState([])
  const [country, setCountry] = useState("worldwide")
  const [updates, setUpdates] = useState("")
  const proxyURL = "https://cors-anywhere.herokuapp.com/"

  useEffect(()=> {
    const targetURL = "https://disease.sh/v3/covid-19/countries"
    const getCountries = async () => {
      await fetch(proxyURL + targetURL)
      .then(response => response.json())
      .then(data => {
        setCountries(data.map(country => ({
          name: country.country,
          value: country.countryInfo.iso2
        })))
      })
    }
    getCountries()
  },[])

  const onCountryChange = (event) => {
    const countryCode = event.target.value
    setCountry(countryCode)
    var targetURL = countryCode === "worldwide"? "https://disease.sh/v3/covid-19/all"
    : `https://disease.sh/v3/covid-19/countries/${countryCode}`

    const getCountryData = async () => {
      await fetch(proxyURL + targetURL)
      .then(response => response.json())
      .then(data => {
        console.log(data)
        setUpdates({
          cases: data.cases,
          todayCases: data.todayCases,
          deaths: data.deaths,
          todayDeaths: data.todayDeaths,
          recovered: data.recovered,
          todayRecovered: data.todayRecovered
        })
      })
    }
    getCountryData()
  }

  return (
    <div className="app">
      <div className="left-panel">
        <div className="header">
          <h1>This is the covid tracker</h1>
          <FormControl className="app-dropdown">
            <Select variant="outlined" value={country} onChange={onCountryChange}>
              <MenuItem value="worldwide">Worldwide</MenuItem>
              {countries.map(country => (
                <MenuItem value={country.value}>{country.name}</MenuItem>
              ))}
            </Select>
          </FormControl>
        </div>

        <div className="app-stats">
          <InfoBox title="Coronavirus cases" newCases={updates.todayCases} total={updates.cases} />
          <InfoBox title="Recovered" newCases={updates.todayRecovered} total={updates.recovered} />
          <InfoBox title="Deaths" newCases={updates.todayDeaths} total={updates.deaths} />
        </div>

        <div className="map">
          <Map />
        </div>
      </div>

      <Card className="right-panel">
        <CardContent>
          <h3>Live cases by Country</h3>
          <h3>Worldwide new cases</h3>
        </CardContent>
      </Card>

    </div>
  );
}

export default App;
