import React, {useState, useEffect} from "react"
import BookItem from "./BookItem.js"
// import bookData from "../data/bookData.js"

import "./Container.css"

let bookData = [
  { name: "1984", author: "George Orwell", category: "novel", price: "£10.99" },
  { name: "The Catcher in the Rye", author: "J.D. Salinger", category: "novel", price: "£6.49" },
  { name: "To Kill a Mockingbird", author: "Harper Lee", category: "novel", price: "£7.99" },
  { name: "The Hobbit", author: "J.R.R. Tolkien", category: "fantasy", price: "£8.99" },
  { name: "Lord of the Flies", author: "William Golding", category: "novel", price: "£6.99" },
  { name: "Animal Farm", author: "George Orwell", category: "novel", price: "£5.99" },
  { name: "Brave New World", author: "Aldous Huxley", category: "novel", price: "£7.49" },
  { name: "The Lord of the Rings", author: "J.R.R. Tolkien", category: "fantasy", price: "£12.99" },
  { name: "The Chronicles of Narnia", author: "C.S. Lewis", category: "fantasy", price: "£11.49" },
  { name: "The Da Vinci Code", author: "Dan Brown", category: "thriller", price: "£9.99" },
  { name: "The Girl with the Dragon Tattoo", author: "Stieg Larsson", category: "mystery", price: "£8.49" },
  { name: "Gone Girl", author: "Gillian Flynn", category: "mystery", price: "£7.99" },
  { name: "The Hunger Games", author: "Suzanne Collins", category: "young adult", price: "£6.99" },
  { name: "The Fault in Our Stars", author: "John Green", category: "young adult", price: "£5.99" },
  { name: "The Alchemist", author: "Paulo Coelho", category: "fiction", price: "£7.49" },
  { name: "The Road", author: "Cormac McCarthy", category: "fiction", price: "£6.99" },
  { name: "Moby-Dick", author: "Herman Melville", category: "novel", price: "£9.99" },
  { name: "The Picture of Dorian Gray", author: "Oscar Wilde", category: "novel", price: "£6.49" },
  { name: "Frankenstein", author: "Mary Shelley", category: "novel", price: "£5.99" },
  { name: "The Road Less Traveled", author: "M. Scott Peck", category: "self-help", price: "£8.49" },
  { name: "Think and Grow Rich", author: "Napoleon Hill", category: "self-help", price: "£7.49" }
];

function Container() {

  const [bookData, setBookData] = useState(
    initBookData
  );

  const [sortMethod, setSortMethod] = useState('none');

  function handleDelete(id) {
    const newBookData = bookData.filter(item => item.id !== id);
    setBookData(newBookData);
  }

  function handleSortMethodChanged(value) {
    setSortMethod(value);
    const newBookData = bookData.sort((a, b) => b.name.localeCompare(a.name));
  }

  return (
    <div className="container">
      <div className="container-sorter">
          <label>
          Sort by: {" "}
          <select name="selectSortMethod"
          defaultValue="none"
          value={sortMethod}
          onChange={event => handleSortMethodChanged(event.target.value)}>
            <option value="none">none</option>
            <option value="name">name</option>
            <option value="price">price</option>
          </select>
        </label>
      </div>
      <div className="container-main">
      {bookData.map(function(item, id){
        item["id"] = id;
        item["handleDelete"] = handleDelete;
         return (
            <BookItem {...item} />
         )
      })}
      </div>
    </div>
  );
}

export default Container;
